import React, { useState } from 'react';

const App=()=>{


    const purple= "pink";
    const [bg,setBg] =useState(purple);
    const[name, setName]= useState("Click Me")
    
   const bgChange=()=>{
      // console.log("clicked");
      let newBg= '#34495e';
      setBg(newBg);
      setName('')
    
};

    return(
        <>
        <div style={{ backgroundColor: bg } }>
            <button onClick= {bgChange}>{name}</button>
        </div>
        </>
    );
};
export default App;
