const time= new Date().toLocaleTimeString();

const[ctime, setctime]= useState(time);
 

const UpdateTime=()=>{
    
    setctime(time);
    //console.log('clicked'+ count++);
};

    return(
        <>
        <h1>{time}</h1>
        <button onClick={UpdateTime}>Get Time</button>
        </>
    );
};
